package atividades;

public class PreePosIncr {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int num1 = 0;
		int valor1 = 4 + ++num1;
		System.out.println(valor1);
		
		int num2 = 0;
		int valor2 = 4 + num2++;
		System.out.println(valor2);
		
		int num3 = 0;
		int valor3 = 4 + --num3;
		System.out.println(valor3);
		
		int num4 = 0;
		int valor4 = 4 + num4--;
		System.out.println(valor4);
		
	}

}
